mapper.select:
SELECT , b.name AS agentLevel,,d.name AS agentLevelP FROM sys_user a LEFT JOIN was_agent_level b on a.agentLevelId=b.agentLevelId LEFT JOIN sys_user c on a.companyParentId=c.id LEFT JOIN was_agent_level d on c.agentLevelId=d.agentLevelId
AND find_in_set(#{id},a.companyParentIds) AND a.del_flag = '0' AND c.del_flag = '0' AND b.state = 1 AND (d.state = 1 or d.state is null) AND (a.state=0 or a.state=1)
AND a.companyName LIKE '%'||#{companyName}||'%' '%'+#{companyName}+'%' concat('%',#{companyName},'%')
AND a.create_date BETWEEN #{startDate} AND #{endDate}
AND a.create_date >= #{startDate}
AND a.create_date <= #{endDate}
ORDER BY ${page.orderBy}
order by a.create_date DESC

   select * FROM sys_user a


			AND binary  a.companyName = #{companyName}				


			AND binary  a.login_name = #{login_name}

SELECT delegateAgent FROM sys_user a 
LEFT JOIN was_agent_level b on a.agentLevelId=b.agentLevelId 

a.id = #{id}

mapper.update:
UPDATE sys_user a SET
a.remarks = #{remarks},
a.state = #{state},
a.update_by = #{update_by}, 
a.update_date = #{update_date}
WHERE a.id = #{id}

UPDATE sys_user a SET
a.endDate = #{endDate},
a.update_by = #{update_by}, 
a.update_date = #{update_date}
WHERE a.id = #{id}

UPDATE sys_user a SET
a.del_flag = #{del_flag},
a.update_by = #{update_by}, 
a.update_date = #{update_date}
WHERE a.id = #{id}
